module.exports.Lexeme = class Lexeme {
	constructor(category, type, raw) {
		this.category = category;
		this.type = type;
		this.raw = raw;
	}

	matches(lexeme) {
		return this.category == lexeme.category
			&& this.type == lexeme.type;
	}

	dump() {
		console.log(
			`(${this.category}, ${this.type}, '${this.raw}')`
		);
	}
}

module.exports.dump_lexemes = function(lexemes) {
	for (let l of lexemes) { l.dump(); }
}
