var $ = require('jquery');
var BaseValidator = require('./basevalidator.js');

module.exports = class BlacklistValidator extends BaseValidator {
	/*
	*  Validate against a set of disallowed values.
	*
	*  Settings:
	*    * bl = The blacklist to use.  (array)
	*/
	constructor(...args) {
		super(...args);
		this.chk_settings(['bl']);
	}

	validate(selector) {
		var ret = true;
		var bl = this.settings.bl;
		selector.query.each(function() {
			if (bl.includes($(this).val())) {
				ret = false;
				return false;
			}
		});
		return ret;
	}
}
