var $ = require('jquery');
var BaseValidator = require('./basevalidator.js');

module.exports = class EqValidator extends BaseValidator {
	/*
	*  Validate all the selected inputs to have the same value.
	*  This validator doesn't need any settings.
	*/
	validate(selector) {
		var ret = true;
		var v = null;
		selector.query.each(function() {
			if (v == null) {
				v = $(this).val();
			} else if (v != $(this).val()) {
				ret = false;
				return false;
			}
		});
		return ret;
	}
}
